/** * Object for Map data */public class country{    /**     * Name of line     */    public String Name;    /**     * Longitudes     */    /**     * Longitudes     */    /**     * Latitudes     */    public short[] lats,longs;    /**     * Line on map     *      * @param latlon Latitiude and Longitude in 2D array     */        country(short[][] latlon){        lats=latlon[0];        longs=latlon[1];    }    /**     * Line on map     *      * @param lat Latitudes     * @param lon Longitudes     */    country(short[] lat,short[] lon){        lats=lat;        longs=lon;    }/** * Get latitudes from line *  * @return Latitudes */   public float[] getLats(){      int n=lats.length;      float[] ans=new float[n];      for(int i=0;i<n;i++){         ans[i]=(float)lats[i]/100;      }      return ans;   }   /**    * Get longitudes from line    *     * @return Longitudes    */   public float[] getLongs(){      int n=longs.length;      float[] ans=new float[n];      for(int i=0;i<n;i++){         ans[i]=(float)longs[i]/100;      }      return ans;   }}