import java.util.*;/** * Object for holding plotting data */public class dataset{    public String name;    public String units;    public int number;    public int colour;    public int symbol;    public Vector data;    public float currentvalue;    public int datalength;        dataset(int pn,String n, String u,int c,int s){        data=new Vector();        number=pn;        units=u;        name=n;        datalength=0;        colour=c;        symbol=s;    }        dataset(int pn,String n, String u){        this(pn,n,u,0,-1);    }        public void addPoint(float p){        data.addElement(new Float(p));        datalength++;    }            public float[] getdata(){        int n=data.size();        float[] a=new float[n];        for(int i=0;i<n;i++){            Float p=(Float)data.elementAt(i);            a[i]=p.floatValue();        }        return a;    }        public float getdatapoint(int pos){            Float p=(Float)data.elementAt(pos);            return p.floatValue();    }        public float[] getdata(int start){        int n=data.size()-start;        float[] a=new float[n];        for(int i=0;i<n;i++){            Float p=(Float)data.elementAt(i+start);            a[i]=p.floatValue();        }        return a;    }}